import React, { useEffect, useState } from 'react'

const Notification = (props) => {

    const [counter, setCounter] = useState(3)

    useEffect(() => {
        setTimeout(() => {
            if (counter > 0) setCounter(counter - 1)
        }, 1000)
    }, [counter])

    return (
        <>
            {
                counter !== 0 && (
                    <div className={`notify ${props.msgType}`}>
                        <h6>{props.msg}</h6>
                    </div>
                )
            }
        </>
    )
}

export default Notification
