        <>
            <div className={`notify ${props.msgType}`}>
                <h6>{props.msg}</h6>
            </div>
            {/* {
                counter !== 0 && (
                    <div className={`notify ${props.msgType}`}>
                        <h6>{props.msg}</h6>
                    </div>
                )
            } */}
        </>
    )
}

export default Notification
