import React, { useState } from 'react'
import crossBtn from '../assets/crossBtn.png'

const Notification = (props) => {
    const [active, setActive] = useState(false)
    const toggle = () => {
        setActive(!active)
    }

    return (
        <>
            {props.active }
            <div className={`notify ${props.msgType} ${active ? 'none' : 'block'} `}>
                <h6>{props.msg}</h6>
                <img src={crossBtn} alt="cross notification" onClick={toggle} />
            </div>
        </>
    )
}

export default Notification
