import React, { useState } from 'react'
import Input from './Input'
import Select from './Select'
import style from './Form.module.css'

const Form = () => {
    const [fname, setFname] = useState('')
    const [lname, setLname] = useState('')
    const [select, setSelect] = useState('')
    const [username, setUsername] = useState('')
    const [password, setPassword] = useState('')
    const [Cpassword, setCpassword] = useState('')
    const [email, setEmail] = useState('')
    const [contact, setContact] = useState('')

    const submitHandler = (e) => {
        e.preventDefault()
        console.log(fname, lname, select, username, password, Cpassword, email, contact)

        if (fname && lname && select && username && password && Cpassword && email && contact) {
            if (password === Cpassword) {
                localStorage.setItem('user', ([{
                    firstName: fname,
                    lastName: lname,
                    department: select,
                    email,
                    contact
                }]))
                alert('Form submitted!')
            }
        }
        else {
            alert('All fields are required!')
        }
    }

    return (
        <div className={style.box}>
            <form action="" onSubmit={submitHandler}>
                <Input label='First Name' type='text' placeholder='First Name' setValue={(e) => setFname(e.target.value)} />

                <Input label='Last Name' type='text' placeholder='Last Name' setValue={(e) => setLname(e.target.value)} />

                <Select label='Select your Department/Office' options={['HR', 'IT', 'Sales']} setValue={(e) => setSelect(e.target.value)} />

                <Input label='Username' type='text' placeholder='Username' setValue={(e) => setUsername(e.target.value)} />

                <Input label='Password' type='password' placeholder='Password' setValue={(e) => setPassword(e.target.value)} />

                <Input label='Confirm Password' type='password' placeholder='Confirm Password' setValue={(e) => setCpassword(e.target.value)} />

                <Input label='E-mail Address' type='email' placeholder='E-mail Address' setValue={(e) => setEmail(e.target.value)} />

                <Input label='Contact no' type='text' placeholder='Contact no' setValue={(e) => setContact(e.target.value)} />

                <div className={style.btnBox}>
                    <button type='submit' className={style.btn}>Submit</button>
                </div>
            </form>
        </div>
    )
}

export default Form
