import React, { useEffect, useState } from 'react'

const Notification = (props) => {

    const [counter, setCounter] = useState(1)

    useEffect(() => {
        setTimeout(() => {
            if (counter > 0) setCounter(counter - 1)
            console.log(counter)
        }, 1000)
    }, [counter])

    return (
        <div
            className={`notify ${props.msgType}`}
            style={`${counter === 0 }`}
        >
            <h6>{props.msg}</h6>
        </div>
    )
}

export default Notification
